const postReducer = (
    state={posts:null,loading:false,error:false,uploading:false},
    action
)=>{
    switch(action.type){
        case "UPLOAD_START":
            return {...state,error:false,uploading:true}
        case "UPLOAD_SUCCESS":
            return {...state,posts:[, ...state.posts],error:false,uploading:false}
        case "UPLOAD_FAILURE":
            return {...state,uploading:false,error:true}
        default:
            return state
    }
}

export default postReducer
