import React, { useState } from 'react'
import { useDispatch, useSelector } from 'react-redux'
import { followUser, unFollowUser } from '../../actions/userAction'

const User = ({person}) => {
  const serverPublic = process.env.REACT_APP_PUBLIC_FOLDER
  const dispatch = useDispatch()
  const {user} = useSelector((state)=>state.authReducer.authData)
  const [following,useFollowing] = useState(person.followers.includes(user._id))

  const handleFollow = ()=>{
    following
      ? dispatch(unFollowUser(person._id,user))
      :dispatch(followUser(person._id,user))

      setFollowing((prev)=>!prev)
  }

  return (
    <div className="follower">
        <div>
            <img 
                 src={person.profilePicture ? serverPublic+person.profilePicture : serverPublic+'defaultProfile.png'} 
                 alt=""
                 className='followerImg'
            />
            <div className="name">
                <span>{person.firstName}</span>
                <span>{person.username}</span>
            </div>
        </div>
        <button className='button followBtn' onClick={handleFollow}>
            {following ? "Unfollow" : "Follow"}
        </button>
    </div>
  )
}

export default User
